import React, { FC, useState, useEffect, useCallback } from "react";
import {
    Button, TextField, Dialog, DialogActions, DialogContent, DialogContentText,
    DialogTitle, AppBar, Toolbar, Typography
} from '@mui/material'
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import AddCircleIcon from "@mui/icons-material/AddCircle";

interface AddTaskFormProps {
    formIsOpen: boolean;
    handleClose: () => void;
    addTask: (task) => void;
}

const AddTaskForm: FC<AddTaskFormProps> = ({ formIsOpen, handleClose, addTask }) => {
    return (
      <Dialog
        open={formIsOpen}
        onClose={handleClose}
        sx={{ p: 0 }}
        PaperProps={{
          component: "form",
          onSubmit: (e) => {
            e.preventDefault();
            console.log(e.target[0].value);
            addTask(e.target[0].value);
          }
        }}
      >
        <DialogTitle
          sx={{ m: 0, p: 0 }}
        >
          <Stack alignItems="center" direction="row" spacing={2}>
          <AppBar position="static">
            <Toolbar>
              <AddCircleIcon />
              <Typography variant="h6">
                Add Task
              </Typography>
            </Toolbar>
          </AppBar>
          </Stack>
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please enter the task you would like to add
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="task"
            name="task"
            label="Title"
            type="text"
            fullWidth
          />
          <TextField
            margin="dense"
            id="description"
            name="description"
            label="Description"
            type="text"
            fullWidth
          />
          <TextField
            margin="dense"
            id="date"
            name="date"
            label="Date"
            type="date"
            defaultValue={new Date().toISOString().split('T')[0]}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button type="submit">Add</Button>
        </DialogActions>
      </Dialog>
    )
}

export default AddTaskForm;
