import React, { FC, useState, useEffect, useCallback } from "react";
import {
    Button, TextField, Dialog, DialogActions, DialogContent, DialogContentText,
    DialogTitle, AppBar, Toolbar, Typography, Stack, Radio, RadioGroup, FormControlLabel
} from '@mui/material'
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import AddCircleIcon from "@mui/icons-material/AddCircle";

interface AddTaskFormProps {
    formIsOpen: boolean;
    handleClose: () => void;
    addTask: (task) => void;
}

const AddTaskForm: FC<AddTaskFormProps> = ({ formIsOpen, handleClose, addTask }) => {
    return (
      <Dialog
        open={formIsOpen}
        onClose={handleClose}
        sx={{ p: 0 }}
        PaperProps={{
          component: "form",
          onSubmit: (e) => {
            e.preventDefault();
            console.log(e.FormData);
            addTask(e.FormData);
          }
        }}
      >
        <DialogTitle
          sx={{ m: 0, p: 0 }}
        >
          <Stack alignItems="center" direction="row" spacing={2}>
            <AppBar position="static">
              <Toolbar>
                <AddCircleIcon />
                <Typography mt={.5} ml={2} variant="h6">
                  Add Task
                </Typography>
              </Toolbar>
            </AppBar>
          </Stack>
        </DialogTitle>
        <DialogContent>
          <DialogContentText mt={2}>
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="title"
            name="title"
            label="Title"
            type="text"
            fullWidth
          />
          <TextField
            margin="dense"
            id="description"
            name="description"
            label="Description"
            type="text"
            fullWidth
          />
          <TextField
            margin="dense"
            id="date"
            name="date"
            label="Date"
            type="date"
            defaultValue={new Date().toISOString().split('T')[0]}
            fullWidth
          />
          <RadioGroup row aria-label="priority" name="priority" defaultValue="low">
            <FormControlLabel value="low" control={<Radio />} label="Low" />
            <FormControlLabel value="medium" control={<Radio />} label="Medium" />
            <FormControlLabel value="high" control={<Radio />} label="High" />
          </RadioGroup>
        </DialogContent>
        <DialogActions mt={-2}>
          <Button onClick={handleClose}>Cancel</Button>
          <Button type="submit">Add</Button>
        </DialogActions>
      </Dialog>
    )
}

export default AddTaskForm;
