import React, { FC, useState, useEffect, useCallback } from "react";
import {
    Button, TextField, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
    DatePicker
} from '@mui/material'

interface AddTaskFormProps {
    formIsOpen: boolean;
    handleClose: () => void;
    addTask: (task: string) => void;
}

const AddTaskForm: FC<AddTaskFormProps> = ({ formIsOpen, handleClose, addTask }) => {
    return (
      <Dialog
        open={formIsOpen}
        onClose={handleClose}
        PaperProps={{
          component: "form",
          onSubmit: (e) => {
            e.preventDefault();
            console.log(e.target[0].value);
            addTask(e.target[0].value);
          }
        }}
      >
        <DialogTitle>Add Task</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please enter the task you would like to add
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="task"
            name="task"
            label="Title"
            type="text"
            fullWidth
          />
          <TextField
            margin="dense"
            id="description"
            name="description"
            label="Description"
            type="text"
            fullWidth
          />
          <DatePicker label="Due Date" />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button type="submit">Add</Button>
        </DialogActions>
      </Dialog>
    )
}

export default AddTaskForm;
