import React, { FC, useState, useEffect, useCallback } from "react";
import { toast } from "react-toastify";
import { 
    Button, Grid, AppBar, Box, Toolbar, IconButton, Typography,
    Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import "react-toastify/dist/ReactToastify.css";
import Title from "../TitleBar/TitleBar.tsx";
import AddTaskForm from "../Form/AddTaskForm.tsx";
import "./styles.css";

interface HomeProps {}

const Home = () => {
  const [tasks, setTasks] = useState<any>();
  const [formIsOpen, setFormIsOpen] = useState(false);

  const openForm = () => {
    setFormIsOpen(true);
  }

  const addTask = (task: string) => {
    setTasks([...tasks, task]);
    toast.success("Task added successfully");
    setFormIsOpen(false);
  }

  const handleClose = () => {
    setFormIsOpen(false);
  }

  return (
    <div>
      <Title
        handleOpenForm={openForm}
      />
      <div>{formIsOpen ? "Form is open" : "Form is closed"}</div>
      <AddTaskForm
        formIsOpen={formIsOpen}
        onSubmit={addTask}
        handleClose={handleClose}
      />
    </div>
  );
};

export default Home;
