import React from 'react';
import {
    FC, useState, useEffect, useCallback
} from 'react';
import {
    Button, Grid, AppBar, Box, Toolbar, IconButton, Typography,
    Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
    Checkbox, TableSortLabel
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import 'react-toastify/dist/ReactToastify.css';

interface TaskListProps {
    tasks?: any[];
    editTaskClick: (task) => void;
    deleteTask: (task) => void;
}

const TaskList: FC<TaskListProps> = ({ tasks, editTaskClick, deleteTask }) => {
    const [checked, setChecked] = useState(false);

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell align="center">Task</TableCell>
                        <TableCell align="center">Description</TableCell>
                        <TableCell align="center">Deadline</TableCell>
                        <TableCell align="center">Priority</TableCell>
                        <TableCell align="center">Is Complete</TableCell>
                        <TableCell align="center">Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {tasks.map((task, index) => (
                        <TableRow
                            key={index}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell align="center" component="th" scope="row">
                                {task.title}
                            </TableCell>
                            <TableCell align="center">{task.description}</TableCell>
                            <TableCell align="center">{task.date}</TableCell>
                            <TableCell align="center">{task.priority}</TableCell>
                            <TableCell align="center">
                                <Checkbox
                                    checked={checked}
                                    onChange={(e) => {
                                        setChecked(e.target.checked);
                                    }}
                                />
                            </TableCell>
                            <TableCell align="center">
                            {!checked && (<Button
                                    variant="contained"
                                    color="primary"
                                    startIcon={<EditIcon />}
                                    onClick={() => {
                                        editTaskClick(task)
                                    }}
                                >
                                    Edit
                                </Button>)}
                                <Box ml={2} />
                                <Button
                                    variant="contained"
                                    color="error"
                                    startIcon={<DeleteIcon />}
                                    onClick={() => {
                                        deleteTask(task)
                                    }}
                                >
                                    Delete
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default TaskList;
