import React, { FC, useState, useEffect, useCallback } from "react";
import { toast } from "react-toastify";
import { 
    Button, Grid, AppBar, Box, Toolbar, IconButton, Typography,
    Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import "react-toastify/dist/ReactToastify.css";
import Title from "../TitleBar/TitleBar.tsx";
import TaskForm from "../Form/TaskForm.tsx";
import TaskList from "../List/TaskList.tsx";
import "./styles.css";

interface HomeProps {}

const Home = () => {
  const [tasks, setTasks] = useState<any>([]);
  const [taskToEdit, setTaskToEdit] = useState<any>(null);
  const [formIsOpen, setFormIsOpen] = useState(false);

  const openForm = () => {
    setFormIsOpen(true);
  }

  const handleSubmit = (task) => {
    if (taskToEdit) {
        editTask(task);
    } else {
        addTask(task);
    }
  }

  const addTask = (task) => {
    const newId = tasks.length + 1;
    task.id = newId;
    setTasks([...tasks, task]);
    toast.success("Task added successfully");
    setFormIsOpen(false);
  }

  const editTaskClick = (task) => {
    setTaskToEdit(task);
    setFormIsOpen(true);
    const newTasks = tasks.map((t) => {
        if (t.id === task.id) {
            return task;
        }
        return t;
    });
    toast.success("Task updated successfully");
  }

  const deleteTask = (task) => {
    const newTasks = tasks.filter((t) => t.id !== task.id);
    setTasks(newTasks);
    toast.success("Task deleted successfully");
  }

  const handleClose = () => {
    setFormIsOpen(false);
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Title
        handleOpenForm={openForm}
      />
      <TaskList
        tasks={tasks}
        editTask={editTask}
        deleteTask={deleteTask}
      />
      <TaskForm
        formIsOpen={formIsOpen}
        handleClose={handleClose}
        addTask={addTask}
      />
    </Box>
  );
};

export default Home;
