import React from 'react';
import {
    FC, useState, useEffect, useCallback
} from 'react';
import {
    Button, Grid, AppBar, Box, Toolbar, IconButton, Typography,
    Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
    Checkbox, TableSortLabel
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import 'react-toastify/dist/ReactToastify.css';

interface TaskListProps {
    tasks?: any[];
    editTaskClick: (task) => void;
    deleteTask: (task) => void;
}

const TaskList: FC<TaskListProps> = ({ tasks, editTaskClick, deleteTask }) => {
    const [checked, setChecked] = useState(false);
    const [order, setOrder] = useState<"asc" | "desc">('asc');
    const [orderBy, setOrderBy] = useState('title');
    const headCells = [
        { id: 'title', label: 'Task' },
        { id: 'description', label: 'Description' },
        { id: 'date', label: 'Deadline' },
        { id: 'priority', label: 'Priority' },
        { id: 'isComplete', label: 'Is Complete' },
        { id: 'actions', label: 'Actions' },
    ];

    const handleRequestSort = (event, property) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
    }

    const createSortHandler = (property) => (event) => {
        handleRequestSort(event, property);
    }

    const onRequestSort = (event, property) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
    }

    const NewTableHead = () => {
        return (
            <TableHead>
                <TableRow>
                    {headCells.map((headCell) => (
                        <TableCell
                            key={headCell.id}
                            align="center"
                        >
                            {headCell.label}
                        </TableCell>
                    ))}
                </TableRow>
            </TableHead>
        );
    }

    return (
        <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <NewTableHead />
                <TableBody>
                    {tasks.map((task, index) => (
                        <TableRow
                            key={index}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        >
                            <TableCell align="center" component="th" scope="row">
                                {task.title}
                            </TableCell>
                            <TableCell align="center">{task.description}</TableCell>
                            <TableCell align="center">{task.date}</TableCell>
                            <TableCell align="center">{task.priority}</TableCell>
                            <TableCell align="center">
                                <Checkbox
                                    checked={task.checked}
                                    onChange={(e) => {
                                        setChecked(e.target.checked);
                                    }}
                                />
                            </TableCell>
                            <TableCell align="center">
                            {!checked && (<Button
                                    variant="contained"
                                    color="primary"
                                    startIcon={<EditIcon />}
                                    onClick={() => {
                                        editTaskClick(task)
                                    }}
                                >
                                    Edit
                                </Button>)}
                                <Box ml={2} />
                                <Button
                                    variant="contained"
                                    color="error"
                                    startIcon={<DeleteIcon />}
                                    onClick={() => {
                                        deleteTask(task)
                                    }}
                                >
                                    Delete
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default TaskList;
